<template>

  <div class='container'>
    <div class='row'>

      <div class="row clear">
        <div class="col-md-1"></div>
        <div class="col-md-12">
          <div class="panel panel-default">
            <div class="panel-heading">最新评论</div>
            <div class="panel-body">
              <ul class="media-list">
                <li class="media">
                  <div class="media-left">
                    <a href="#"> <img v-if='profile.headUrl==null' alt="profile.uname" class="media-object" src="data:image/svg+xml;base64,"
                        data-holder-rendered="true" style="width: 64px; height: 64px;">

                      <img v-if='profile.headUrl!=null' alt="{{profile.uname}}" class="media-object" style="width: 64px; height: 64px;">
                    </a>
                  </div>
                  <div class="media-body">
                    <h4 class="media-heading">{{profile.uname}}</h4>
                    <textarea class="form-control" v-model="currentMessage" rows="2"></textarea>
                    <div class='margin-8'>
                      <button type="button" class="btn btn-success float-right" @click = "post" >&nbsp&nbsp&nbsp提交&nbsp&nbsp&nbsp;</button>
                    </div>
                  </div>
                </li>
                <li class="media" v-for="item in comments">
                  <div class="media-left">
                    <a href="#"> <img v-if='item.headUrl==null' alt="item.uname" class="media-object" src="data:image/svg+xml;base64,"
                        data-holder-rendered="true" style="width: 64px; height: 64px;">

                      <img v-if='item.headUrl!=null' alt="{{item.uname}}" class="media-object" style="width: 64px; height: 64px;">
                    </a>
                  </div>
                  <div class="media-body">
                    <h4 class="media-heading">{{item.uname}}</h4>
                    <span>{{item.time | time}}</span>
                    <p>{{item.text}}</p>
                  </div>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>

import Wilddog from 'wilddog'
var href =  window.location.href.split('/').join('-');
var ref = new Wilddog('http://test123.wilddogio.com/freee-comments/'+href);
export default {
  data () {
    return {
      currentMessage: '',
      authMethod:null,
      profile:{
        uname:'jackxy',
        headUrl:null,
      },
      
      comments:[

      ]
    }
  },
  components:{

  },
  ready(){
    var self = this;
    ref.on('child_added',function(snapshot){
      var value = snapshot.val();
      self.comments.unshift(value);
    });
  },
  methods:{
    post(){
      var message = this.currentMessage;
      var uname = this.profile.uname;
      var time = new Date().getTime();
      ref.push({
        text:message,
        uname:uname,
        time:time
      },(err)=>{
        if(err){
          //TODO 
        }
        else{
          this.currentMessage = '';
        }
      });

    }
  }
}
</script>

